INSERT [dbo].[Capabilities] ([Domain], [Container], [Capability], [Capability2], [UniqueId], [Description], [Notes], [Scopes], [C], [I], [A], [ResponsibilityVector], [OtherActors]) VALUES ( N'BOSS', N'Security Monitoring Services', N'Event Mining', N'', N'Event Mining', N'A capability to perform statistical and trend analysis of events to determine patterns of normal and abnormal behavior.  This capability supports not only identification of anomalous activities but also aids in the establishment of normative baselines.  ', N'The controls in the "Capability implementation" columns are in accordance with FedRAMP & SP 800-53 baselines, however, event mining might not be implemented for the low or moderate impact information. If the decission is made that this capability is necessary for a low or moderate impact system, the controls from the high impact baseline listed here will be necessary.', N'CoMo; TIC', 2, 2, 2, N'X,A,X,X,A,X', N'B,B,B,A,')
INSERT [dbo].[Capabilities] ([Domain], [Container], [Capability], [Capability2], [UniqueId], [Description], [Notes], [Scopes], [C], [I], [A], [ResponsibilityVector], [OtherActors]) VALUES ( N'BOSS', N'Security Monitoring Services', N'Event Correlation', N'', N'Event Correlation', N'A capability to analyze and associate an event from one source with events from the same or other sources to derive additional information or detect patterns of activity.', N'', N'CoMo; TIC', 2, 3, 2, N'X,A,X,X,A,X', N'B,B,B,X,')
