INSERT [dbo].[Capabilities] ([Domain], [Container], [Capability], [Capability2], [UniqueId], [Description], [Notes], [Scopes], [C], [I], [A], [ResponsibilityVector], [OtherActors]) VALUES ( N'BOSS', N'Security Monitoring Services', N'End-Point Monitoring', N'', N'End-Point Monitoring', N'A capability to collect events associated with end user system interaction including logins, queries, transactions, administrative activity for analysis and threat monitoring purposes.', N'', N'CoMo', 2, 3, 3, N'X,A,X,A,X,A', N'B,B,B,A,')
