INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 61, N'(1)', N'The organization establishes the following restrictions on the use of open source software: [Assignment: organization-defined restrictions].', N'Open source software refers to software that is available in source code form. Certain software rights normally reserved for copyright holders are routinely provided under software license agreements that permit individuals to study, change, and improve the software. From a security perspective, the major advantage of open source software is that it provides organizations with the ability to examine the source code. However, there are also various licensing issues associated with open source software including, for example, the constraints on derivative use of such software.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 61, N'a.', N'Uses software and associated documentation in accordance with contract agreements and copyright laws;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 61, N'b.', N'Tracks the use of software and associated documentation protected by quantity licenses to control copying and distribution; and', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 61, N'c.', N'Controls and documents the use of peer-to-peer file sharing technology to ensure that this capability is not used for the unauthorized distribution, display, performance, or reproduction of copyrighted work.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 62, N'(1)', N'The information system alerts [Assignment: organization-defined personnel or roles] when the unauthorized installation of software is detected.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 62, N'(2)', N'The information system prohibits user installation of software without explicit privileged status.', N'Privileged status can be obtained, for example, by serving in the role of system administrator.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 62, N'a.', N'Establishes [Assignment: organization-defined policies] governing the installation of software by users;', N'')
