INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 53, N'(1)(a)', N'[Assignment: organization-defined frequency];', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 53, N'(1)(b)', N'When required due to [Assignment organization-defined circumstances]; and', N'')
