INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 94, N'(2)', N'The organization provides information spillage response training [Assignment: organization-defined frequency].', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 94, N'(3)', N'The organization implements [Assignment: organization-defined procedures] to ensure that organizational personnel impacted by information spills can continue to carry out assigned tasks while contaminated systems are undergoing corrective actions.', N'Correction actions for information systems contaminated due to information spillages may be very time-consuming. During those periods, personnel may not have access to the contaminated systems, which may potentially affect their ability to conduct organizational business.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 94, N'(4)', N'The organization employs [Assignment: organization-defined security safeguards] for personnel exposed to information not within assigned access authorizations.', N'Security safeguards include, for example, making personnel exposed to spilled information aware of the federal laws, directives, policies, and/or regulations regarding the information and the restrictions imposed based on exposure to such information.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 94, N'a.', N'Identifying the specific information involved in the information system contamination;', N'')
