INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 97, N'c.', N'Requires that [Assignment: organization-defined personnel or roles] explicitly approve the removal of the information system or system components from organizational facilities for off-site maintenance or repairs;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 97, N'd.', N'Sanitizes equipment to remove all information from associated media prior to removal from organizational facilities for off-site maintenance or repairs;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 97, N'e.', N'Checks all potentially impacted security controls to verify that the controls are still functioning properly following maintenance or repair actions; and', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 97, N'f.', N'Includes [Assignment: organization-defined maintenance-related information] in organizational maintenance records.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 98, N'(1)', N'The organization inspects the maintenance tools carried into a facility by maintenance personnel for improper or unauthorized modifications.', N'If, upon inspection of maintenance tools, organizations determine that the tools have been modified in an improper/unauthorized manner or contain malicious code, the incident is handled consistent with organizational policies and procedures for incident handling.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 98, N'(2)', N'The organization checks media containing diagnostic and test programs for malicious code before the media are used in the information system.', N'If, upon inspection of media containing maintenance diagnostic and test programs, organizations determine that the media contain malicious code, the incident is handled consistent with organizational incident handling policies and procedures.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 98, N'(3)', N'The organization prevents the unauthorized removal of maintenance equipment containing organizational information by:', N'Organizational information includes all information specifically owned by organizations and information provided to organizations in which organizations serve as information stewards.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 98, N'(3)(a)', N'Verifying that there is no organizational information contained on the equipment;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 98, N'(3)(b)', N'Sanitizing or destroying the equipment;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 98, N'(3)(c)', N'Retaining the equipment within the facility; or', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 98, N'(3)(d)', N'Obtaining an exemption from [Assignment: organization-defined personnel or roles] explicitly authorizing removal of the equipment from the facility.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 98, N'(4)', N'The information system restricts the use of maintenance tools to authorized personnel only.', N'This control enhancement applies to information systems that are used to carry out maintenance functions.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(1)', N'The organization:', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(1)(a)', N'Audits nonlocal maintenance and diagnostic sessions [Assignment: organization-defined audit events]; and', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(1)(b)', N'Reviews the records of the maintenance and diagnostic sessions.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(2)', N'The organization documents in the security plan for the information system, the policies and procedures for the establishment and use of nonlocal maintenance and diagnostic connections.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(3)', N'The organization:', N'Comparable security capability on information systems, diagnostic tools, and equipment providing maintenance services implies that the implemented security controls on those systems, tools, and equipment are at least as comprehensive as the controls on the information system being serviced.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(3)(a)', N'Requires that nonlocal maintenance and diagnostic services be performed from an information system that implements a security capability comparable to the capability implemented on the system being serviced; or', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(3)(b)', N'Removes the component to be serviced from the information system and prior to nonlocal maintenance or diagnostic services, sanitizes the component (with regard to organizational information) before removal from organizational facilities, and after the service is performed, inspects and sanitizes the component (with regard to potentially malicious software) before reconnecting the component to the information system.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(4)', N'The organization protects nonlocal maintenance sessions by:', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(4)(a)', N'Employing [Assignment: organization-defined authenticators that are replay resistant]; and', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(4)(b)', N'Separating the maintenance sessions from other network sessions with the information system by either:', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(4)(b)(1)', N'Physically separated communications paths; or', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(4)(b)(2)', N'Logically separated communications paths based upon encryption.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(5)', N'The organization:', N'Notification may be performed by maintenance personnel. Approval of nonlocal maintenance sessions is accomplished by organizational personnel with sufficient information security and information system knowledge to determine the appropriateness of the proposed maintenance.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(5)(a)', N'Requires the approval of each nonlocal maintenance session by [Assignment: organization-defined personnel or roles]; and', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(5)(b)', N'Notifies [Assignment: organization-defined personnel or roles] of the date and time of planned nonlocal maintenance.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(6)', N'The information system implements cryptographic mechanisms to protect the integrity and confidentiality of nonlocal maintenance and diagnostic communications.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'(7)', N'The information system implements remote disconnect verification at the termination of nonlocal maintenance and diagnostic sessions.', N'Remote disconnect verification ensures that remote connections from nonlocal maintenance sessions have been terminated and are no longer available for use.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'a.', N'Approves and monitors nonlocal maintenance and diagnostic activities;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'b.', N'Allows the use of nonlocal maintenance and diagnostic tools only as consistent with organizational policy and documented in the security plan for the information system;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'c.', N'Employs strong authenticators in the establishment of nonlocal maintenance and diagnostic sessions;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 99, N'd.', N'Maintains records for nonlocal maintenance and diagnostic activities; and', N'')
