INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 120, N'(1)', N'The organization provides emergency lighting for all areas within the facility supporting essential missions and business functions.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 121, N'(1)', N'The organization employs fire detection devices/systems for the information system that activate automatically and notify [Assignment: organization-defined personnel or roles] and [Assignment: organization-defined emergency responders] in the event of a fire.', N'Organizations can identify specific personnel, roles, and emergency responders in the event that individuals on the notification list must have appropriate access authorizations and/or clearances, for example, to obtain access to facilities where classified operations are taking place or where there are information systems containing classified information.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 121, N'(2)', N'The organization employs fire suppression devices/systems for the information system that provide automatic notification of any activation to Assignment: organization-defined personnel or roles] and [Assignment: organization-defined emergency responders].', N'Organizations can identify specific personnel, roles, and emergency responders in the event that individuals on the notification list must have appropriate access authorizations and/or clearances, for example, to obtain access to facilities where classified operations are taking place or where there are information systems containing classified information.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 121, N'(3)', N'The organization employs an automatic fire suppression capability for the information system when the facility is not staffed on a continuous basis.', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 121, N'(4)', N'The organization ensures that the facility undergoes [Assignment: organization-defined frequency] inspections by authorized and qualified inspectors and resolves identified deficiencies within [Assignment: organization-defined time period].', N'')
