INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 112, N'b.', N'Maintains physical access audit logs for [Assignment: organization-defined entry/exit points];', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 112, N'c.', N'Provides [Assignment: organization-defined security safeguards] to control access to areas within the facility officially designated as publicly accessible;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 112, N'd.', N'Escorts visitors and monitors visitor activity [Assignment: organization-defined circumstances requiring visitor escorts and monitoring];', N'')
