INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 115, N'(2)', N'The organization employs automated mechanisms to recognize [Assignment: organization-defined classes/types of intrusions] and initiate [Assignment: organization-defined response actions].', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 115, N'(3)', N'The organization employs video surveillance of [Assignment: organization-defined operational areas] and retains video recordings for [Assignment: organization-defined time period].', N'This control enhancement focuses on recording surveillance video for purposes of subsequent review, if circumstances so warrant (e.g., a break-in detected by other means). It does not require monitoring surveillance video although organizations may choose to do so. Note that there may be legal considerations when performing and retaining video surveillance, especially if such surveillance is in a public location.')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 115, N'(4)', N'The organization monitors physical access to the information system in addition to the physical access monitoring of the facility as [Assignment: organization-defined physical spaces containing one or more components of the information system].', N'This control enhancement provides additional monitoring for those areas within facilities where there is a concentration of information system components (e.g., server rooms, media storage areas, communications centers).')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 115, N'a.', N'Monitors physical access to the facility where the information system resides to detect and respond to physical security incidents;', N'')
INSERT [dbo].[Specs] ([ControId], [SpecificationlName], [Description], [Guidance]) VALUES ( 115, N'b.', N'Reviews physical access logs [Assignment: organization-defined frequency] and upon occurrence of [Assignment: organization-defined events or potential indications of events]; and', N'')
